function (user, context, callback) {
  getRoles(user.email, function(err, roles) {
    if (err) return callback(err);

    user.roles = roles;

    callback(null, user, context);
  });

  // Queries a table by e-mail and returns associated 'Roles'
  function getRoles(email, done) {
    var connection = sqlserver.connect({
      userName:  '<user_name>',
      password:  '<password>',
      server:    '<db_server_name>',
      options: {
        database: '<db_name>',
        encrypt:  true,
        rowCollectionOnRequestCompletion: true
      }
    }).on('errorMessage', function (error) {
      console.log(error.message);
    });

    var query = "SELECT Email, Role " +
                "FROM dbo.Role WHERE Email = @email";

    connection.on('connect', function (err) {
      if (err) return done(new Error(err));

      var request = new sqlserver.Request(query, function (err, rowCount, rows) {
        if (err) return done(new Error(err));

        var roles = rows.map(function (row) {
          return row[1].value;
        });

        done(null, roles);
      });

      request.addParameter('email', sqlserver.Types.VarChar, email);

      connection.execSql(request);
    });
  }
}

function (user, context, callback) {
  user.app_metadata = user.app_metadata || { };

  var private_data = decrypt(user.app_metadata.private_data);
  if (private_data.license_key === '1234567') {
    user.role = 'admin';
  }

  return callback(null, user, context);

  function decrypt(data) {
    if (!data) {
      return { };  
    }
    var iv = new Buffer(configuration.ENCRYPT_IV);
    var encodeKey = crypto.createHash('sha256')
    .update(configuration.ENCRYPT_PASSWORD, 'utf-8').digest();
    var cipher = crypto.createDecipheriv('aes-256-cbc', encodeKey, iv);
    var decrypted = cipher.update(data, 'base64', 'utf8') + cipher.final('utf8');
    return JSON.parse(decrypted);
  }
}

function (user, context, callback) {
  user.app_metadata = user.app_metadata || { };
  user.app_metadata.private_data = encrypt({
    license_key: '1234567',
    social_security_number: '56789'
  });

  callback(null, user, context);

  function encrypt(data) {
    var iv = new Buffer(configuration.ENCRYPT_IV);
    var decodeKey = crypto.createHash('sha256')
      .update(configuration.ENCRYPT_PASSWORD, 'utf-8').digest();
    var cipher = crypto.createCipheriv('aes-256-cbc', decodeKey, iv);
    return cipher.update(JSON.stringify(data || {}), 'utf8', 'base64') + cipher.final('base64');
  }
}

function (user, context, callback) {
  var FULLCONTACT_KEY = 'YOUR FULLCONTACT API KEY';
  var SLACK_HOOK = 'YOUR SLACK HOOK URL';

  var slack = require('slack-notify')(SLACK_HOOK);

  // skip if no email
  if(!user.email) return callback(null, user, context);
  // skip if fullcontact metadata is already there
  if(user.user_metadata && user.user_metadata.fullcontact) return callback(null, user, context);
  request({
    url: 'https://api.fullcontact.com/v2/person.json',
    qs: {
      email:  user.email,
      apiKey: FULLCONTACT_KEY
    }
  }, function (error, response, body) {
    if (error || (response && response.statusCode !== 200)) {

      slack.alert({
        channel: '#slack_channel',
        text: 'Fullcontact API Error',
        fields: {
          error: error ? error.toString() : (response ? response.statusCode + ' ' + body : '')
        }
      });

      // swallow fullcontact api errors and just continue login
      return callback(null, user, context);
    }


    // if we reach here, it means fullcontact returned info and we'll add it to the metadata
    user.user_metadata = user.user_metadata || {};
    user.user_metadata.fullcontact = JSON.parse(body);

    auth0.users.updateUserMetadata(user.user_id, user.user_metadata);
    return callback(null, user, context);
  });
}

