function accessOnWeekdaysOnly(user, context, callback) {

  if (context.clientName === 'TheAppToCheckAccessTo') {
    const date = new Date();
    const d = date.getDay();

    if (d === 0 || d === 6) {
      return callback(new UnauthorizedError('This app is available during the week'));
    }
  }

  callback(null, user, context);
}

function activeDirectoryGroups(user, context, callback) {
  var groupAllowed = 'group1';
  if (user.groups) {
    var userHasAccess = user.groups.some(
      function (group) {
        return groupAllowed === group;
      });

    if (!userHasAccess) {
      return callback(new UnauthorizedError('Access denied.'));
    }
  }

  callback(null, user, context);
}

function (user, context, callback) {
  // This rule adds the authenticated user's email address to the access token.

  var namespace = 'https://example.com/';

  context.accessToken[namespace + 'email'] = user.email;
  return callback(null, user, context);
}

function checkDomainsAgainstConnectionAliases(user, context, callback) {
  const connectionOptions = context.connectionOptions;
  const domainAliases = connectionOptions.domain_aliases || [];
  const tenantDomain = connectionOptions.tenant_domain;

  // No domains -> access allowed
  if (!tenantDomain && !domainAliases.length) {
    return callback(null, user, context);
  }

  // Domain aliases exist but no tenant domain exists
  if (domainAliases.length && !tenantDomain) return callback('Access denied');

  const allowedDomains = new Set([tenantDomain]);
  domainAliases.forEach(function (alias) {
    if (alias) allowedDomains.add(alias.toLowerCase());
  });

  // Access allowed if domain is found
  const emailSplit = user.email.split('@');
  const userEmailDomain = emailSplit[emailSplit.length - 1].toLowerCase();
  if (allowedDomains.has(userEmailDomain)) return callback(null, user, context);

  return callback('Access denied');
}

function checkLastPasswordReset(user, context, callback) {
  function daydiff (first, second) {
    return (second-first)/(1000*60*60*24);
  }

  const last_password_change = user.last_password_reset || user.created_at;

  if (daydiff(new Date(last_password_change), new Date()) > 30) {
    return callback(new UnauthorizedError('please change your password'));
  }
  callback(null, user, context);
}

function disableResourceOwner(user, context, callback) {
  if (context.protocol === 'oauth2-resource-owner') {
    return callback(
      new UnauthorizedError('The resource owner endpoint cannot be used.'));
  }
  callback(null, user, context);
}

function disableSocialSignups(user, context, callback) {

  const CLIENTS_ENABLED = ['REPLACE_WITH_YOUR_CLIENT_ID'];
  // run only for the specified clients
  if (CLIENTS_ENABLED.indexOf(context.clientID) === -1) {
    return callback(null, user, context);
  }

  // initialize app_metadata
  user.app_metadata = user.app_metadata || {};

  const is_social = context.connectionStrategy === context.connection;
  // if it is the first login (hence the `signup`) and it is a social login
  if (context.stats.loginsCount === 1 && is_social) {

    // turn on the flag
    user.app_metadata.is_signup = true;

    // store the app_metadata
    auth0.users.updateAppMetadata(user.user_id, user.app_metadata)
      .then(function(){
        // throw error
        return callback(new Error('Signup disabled'));
      })
      .catch(function(err){
        callback(err);
      });

    return;
  }

  // if flag is enabled, throw error
  if (user.app_metadata.is_signup) {
    return callback(new Error('Signup disabled'));
  }

  // else it is a non social login or it is not a signup
  callback(null, user, context);
}

function dropboxWhitelist(user, context, callback) {
  const request = require('request');

  // Access should only be granted to verified users.
  if (!user.email || !user.email_verified) {
    return callback(new UnauthorizedError('Access denied.'));
  }

  request.get({
    url: 'https://dl.dropboxusercontent.com/u/12345678/email_list.txt'
  }, (err, response, body) => {
    const whitelist = body.split('\n');

    const userHasAccess = whitelist.some(function (email) {
      return email === user.email;
    });

    if (!userHasAccess) {
      return callback(new UnauthorizedError('Access denied.'));
    }

    callback(null, user, context);
  });
}

function emailVerified(user, context, callback) {
  if (!user.email_verified) {
    return callback(new UnauthorizedError('Please verify your email before logging in.'));
  } else {
    return callback(null, user, context);
  }
}

function ipAddressWhitelist(user, context, callback) {
  const whitelist = ['1.2.3.4', '2.3.4.5']; // authorized IPs
  const userHasAccess = whitelist.some(function (ip) {
    return context.request.ip === ip;
  });

  if (!userHasAccess) {
    return callback(new Error('Access denied from this IP address.'));
  }

  return callback(null, user, context);
}

function setRolesToUser(user, context, callback) {

  // Roles should only be set to verified users.
  if (!user.email || !user.email_verified) {
    return callback(null, user, context);
  }

  user.app_metadata = user.app_metadata || {};
  // You can add a Role based on what you want
  // In this case I check domain
  const addRolesToUser = function (user) {
    const endsWith = '@example.com';

    if (user.email && (user.email.substring(user.email.length - endsWith.length, user.email.length) === endsWith)) {
      return ['admin'];
    }
    return ['user'];
  };

  const roles = addRolesToUser(user);

  user.app_metadata.roles = roles;
  auth0.users.updateAppMetadata(user.user_id, user.app_metadata)
    .then(function () {
      context.idToken['https://example.com/roles'] = user.app_metadata.roles;
      callback(null, user, context);
    })
    .catch(function (err) {
      callback(err);
    });
}

function getBadEmailDetection(user, context, callback) {

    // skip if no email
    if (!user.email) return callback(null, user, context);

    user.user_metadata = user.user_metadata || {};

    const request = require('request');

    console.log('Preparing request to Auth0-Signals');
    request({
      url: 'https://signals.api.auth0.com/bademail/' + user.email + '?timeout=0',
      qs: {
        token:  configuration.AUTH0SIGNALS_API_KEY
      },
      headers: {
        'content-type': 'application/json'
      },
      timeout: 1000
    }, function (err, resp, body) {
      if (err) {
        console.log('Error request to Signals:' + err.code + ', ' + err);
        return callback(null, user, context);
      }
      if (resp.statusCode !== 200) {
        console.log('Signals not returning 200:' + resp.statusCode);
        return callback(null, user, context);
      }
      const signals_response = JSON.parse(body);

      user.user_metadata.email = user.user_metadata.email || {};

      // the full JSON object returned can be found here: https://auth0.com/signals/docs/#email
      user.user_metadata.email = signals_response.response;

      // persist the user_metadata update
      auth0.users.updateUserMetadata(user.user_id, user.user_metadata)
        .then(function(){
            context.idToken['https://example.com/email'] = user.user_metadata.email;
            callback(null, user, context);
        })
        .catch(function(err){
            console.log(err);
            callback(err);
        });
    });
  }

