async function userRiskScore(user, context, cb) {
  // For Logging Events
  const log = global.getLogger
    ? global.getLogger('Get Risk Score', cb)
    : {
        callback: cb,
        error: console.error,
        info: console.log,
        debug: console.log
      };
  const { callback } = log;

  // Skip if not user facing app type
  if (context.clientMetadata.userApp !== 'true') {
    log.info(`Skipping risk check as not enabled for app ${context.clientName}`);
    return callback(null, user, context);
  }

  const MINFRAUD_API = `https://${configuration.MINFRAUD_ACCOUNT_ID}:${configuration.MINFRAUD_LICENSE_KEY}@minfraud.maxmind.com/minfraud/v2.0/score`;

  // Set to false to trigger high risk
  // Will set IP to Russian IP and change user agent.
  const fakeData = false;

  const ipAddress = fakeData ? '87.242.77.197' : context.request.ip;
  const userAgent = fakeData ? 'naughty-fraud-agent' : context.request.userAgent;

  const data = {
    device: {
      ip_address: ipAddress,
      user_agent: userAgent
    },
    account: {
      user_id: user.user_id
    },
    email: {
      address: user.email || ''
    },
    billing: {
      first_name: user.given_name || '',
      last_name: user.last_name || ''
    }
  };

  try {
    const request = require('request-promise');
    const result = await request.post(MINFRAUD_API, {
      body: data,
      json: true,
      timeout: 3000
    });

    const userInfo = `${user.email || user.username} (${user.user_id})`;
    log.info(`Fraud response for user ${userInfo}: ${JSON.stringify(result, null, 2)}`);

    user.risk = {
      score: result.risk_score,
      ip_address: result.ip_address,
      device: result.device,
      email: result.email
    };

    // Append to tokens
    context.idToken['https://travel0.net/risk'] = user.risk;
    context.accessToken['https://travel0.net/risk'] = user.risk;
  } catch (err) {
    // If the service is down, the request failed, or the result is OK just continue.
    log.error(`Error while attempting fraud check: ${err.message}`);
  }
  return callback(null, user, context);
}

function defaultPictureForNullAvatars(user, context, callback) {
  if (user.picture.indexOf('cdn.auth0.com') > -1) {
    const url = require('url');
    const u = url.parse(user.picture, true);
    u.query.d = 'URL_TO_YOUR_DEFAULT_PICTURE_HERE';
    delete u.search;
    user.picture = url.format(u);
  }
  callback(null, user, context);
}

function facebookCustomPicture(user, context, callback) {
  const _ = require('lodash');

  if (context.connection === 'facebook') {
    const fbIdentity = _.find(user.identities, { connection: 'facebook' });
    // for more sizes and types of images that can be returned, see:
    // https://developers.facebook.com/docs/graph-api/reference/user/picture/
    const pictureType = 'large';
    context.idToken.picture = 'https://graph.facebook.com/v2.5/' + fbIdentity.user_id + '/picture?type=' + pictureType;
  }
  callback(null, user, context);
}

function getFullContactProfile(user, context, callback) {
  const FULLCONTACT_KEY = configuration.FULLCONTACT_KEY;
  const SLACK_HOOK = configuration.SLACK_HOOK_URL;

  const request = require('request');
  const slack = require('slack-notify')(SLACK_HOOK);

  // skip if no email
  if (!user.email) return callback(null, user, context);

  // skip if fullcontact metadata is already there
  if (user.user_metadata && user.user_metadata.fullcontact) return callback(null, user, context);

  request.get('https://api.fullcontact.com/v2/person.json', {
    qs: {
      email:  user.email,
      apiKey: FULLCONTACT_KEY
    },
    json: true
  }, (error, response, body) => {
    if (error || (response && response.statusCode !== 200)) {

      slack.alert({
        channel: '#slack_channel',
        text: 'Fullcontact API Error',
        fields: {
          error: error ? error.toString() : (response ? response.statusCode + ' ' + body : '')
        }
      });

      // swallow fullcontact api errors and just continue login
      return callback(null, user, context);
    }

    // if we reach here, it means fullcontact returned info and we'll add it to the metadata
    user.user_metadata = user.user_metadata || {};
    user.user_metadata.fullcontact = body;

    auth0.users.updateUserMetadata(user.user_id, user.user_metadata);
    context.idToken['https://example.com/fullcontact'] = user.user_metadata.fullcontact;
    return callback(null, user, context);
  });
}

