function trackEventsWithSplunkHec(user, context, callback) {
  const request = require('request');

  user.app_metadata = user.app_metadata || {};
  const endpoint = 'https://http-inputs-mysplunkcloud.example.com:443/services/collector'; // replace with your Splunk HEC endpoint;

  //Add any interesting info to the event
  const hec_event = {
    event: {
      message: user.app_metadata.signedUp ? 'Login' : 'SignUp',
      application: context.clientName,
      clientIP: context.request.ip,
      protocol: context.protocol,
      userName: user.name,
      userId: user.user_id
    },
    source: 'auth0',
    sourcetype: 'auth0_activity'
  };

  request.post({
    url: endpoint,
    headers: {
      'Authorization': 'Splunk ' + configuration.SPLUNK_HEC_TOKEN
    },
    strictSSL: true, // set to false if using a self-signed cert
    json: hec_event
  }, function(error, response, body) {
    if (error) return callback(error);
    if (response.statusCode !== 200) return callback(new Error('Invalid operation'));
    user.app_metadata.signedUp = true;
    auth0.users.updateAppMetadata(user.user_id, user.app_metadata)
      .then(function () {
        callback(null, user, context);
      })
      .catch(function (err) {
        callback(err);
      });
  });

}

function updateFirebaseUser(user, context, callback) {
  const request = require('request');

  const baseURL = configuration.FIREBASE_URL;
  const secret = configuration.FIREBASE_SECRET;
  const fb_id = Buffer.from(user.user_id).toString('base64');

  const fbIdentity = {
    identity: {
      user_id: user.user_id,
      email: user.email,
      name: user.name,
      nickname: user.nickname,
      picture: user.picture
    }
  };

  const putURL = baseURL + '/users/' + fb_id + '.json?auth=' + secret;
  request.put({
    url: putURL,
    json: fbIdentity
  },
  function(err, response, body) {
    if (err) return callback(err);
    return callback(null, user, context);
  });
}

function triggerZapOnUserLogin(user, context, callback) {
  const _ = require('lodash');
  const request = require('request');

  const small_context = {
    appName: context.clientName,
    userAgent: context.request.userAgent,
    ip: context.request.ip,
    connection: context.connection,
    strategy: context.connectionStrategy
  };

  const payload_to_zap = _.extend({}, user, small_context);

  request.post({
    url : configuration.ZAP_HOOK_URL,
    json: payload_to_zap
  },
  function (err, response, body) {
    // swallow error
    callback(null, user, context);
  });
}

function triggerZapOnNewUser(user, context, callback) {
  // short-circuit if the user signed up already
  if (context.stats.loginsCount > 1) {
    return callback(null, user, context);
  }

  const _ = require('lodash');
  const request = require('request');

  const small_context = {
    appName: context.clientName,
    userAgent: context.request.userAgent,
    ip: context.request.ip,
    connection: context.connection,
    strategy: context.connectionStrategy
  };

  const payload_to_zap = _.extend({}, user, small_context);

  request.post({
    url: configuration.ZAP_HOOK_URL,
    json: payload_to_zap
  });

  // donâ€™t wait for the Zapier WebHook call to finish, return right away (the request will continue on the sandbox)`
  callback(null, user, context);
}

function duoMultifactor(user, context, callback) {

  var CLIENTS_WITH_MFA = ['REPLACE_WITH_YOUR_CLIENT_ID'];
  // run only for the specified clients
  if (CLIENTS_WITH_MFA.indexOf(context.clientID) !== -1) {
    // uncomment the following if clause in case you want to request a second factor only from user's that have user_metadata.use_mfa === true
    // if (user.user_metadata && user.user_metadata.use_mfa){
      context.multifactor = {
        //required
        provider: 'duo',
        ikey: configuration.DUO_IKEY,
        skey: configuration.DUO_SKEY,
        host: configuration.DUO_HOST, // e.g.: 'api-XXXXXXXX.duosecurity.com',

        // optional, defaults to true. Set to false to force DuoSecurity every time.
        // See https://auth0.com/docs/multifactor-authentication/custom#change-the-frequency-of-authentication-requests for details
        allowRememberBrowser: false,

        // optional. Use some attribute of the profile as the username in DuoSecurity. This is also useful if you already have your users enrolled in Duo.
        // username: user.nickname
      };
    // }
  }

  callback(null, user, context);
}

function guardianMultifactorAuthorization(user, context, callback) {
  if (!user.app_metadata || !user.app_metadata.authorization ||
    !Array.isArray(user.app_metadata.authorization.groups)) {
    return callback(null, user, context);
  }

  const groups = user.app_metadata.authorization.groups;
  const GROUPS_WITH_MFA = {
    // Add groups that need MFA here
    // Example
    'admins': true
  };

  const needsMFA = !!groups.find(function(group) {
    return GROUPS_WITH_MFA[group];
  });

  if (needsMFA){
    context.multifactor = {
      // required
      provider: 'guardian', //required

      // optional, defaults to true. Set to false to force Guardian authentication every time.
      // See https://auth0.com/docs/multifactor-authentication/custom#change-the-frequency-of-authentication-requests for details
      allowRememberBrowser: false
    };
  }

  callback(null, user, context);
}

function guardianMultifactorIpRange(user, context, callback) {
  const ipaddr = require('ipaddr.js');
  const corp_network = "192.168.1.134/26";
  const current_ip = ipaddr.parse(context.request.ip);

  if (!current_ip.match(ipaddr.parseCIDR(corp_network))) {
    context.multifactor = {
      provider: 'guardian',

      // optional, defaults to true. Set to false to force Guardian authentication every time.
      // See https://auth0.com/docs/multifactor-authentication/custom#change-the-frequency-of-authentication-requests for details
      allowRememberBrowser: false
    };
  }

  callback(null, user, context);
}

function (user, context, callback) {

  // This rule initiates multi-factor authenticaiton as a second factor
  // whenever the request contains the following value:
  // 
  // acr_values = 'http://schemas.openid.net/pape/policies/2007/06/multi-factor'
  // 
  // and multi-factor authentication has not already been completed in the
  // current session/

  if (context.request.query.acr_values === 'http://schemas.openid.net/pape/policies/2007/06/multi-factor' && !context.authentication.methods.some(method => method.name === 'mfa')) {

  	context.multifactor = {
        provider: 'any',
        allowRememberBrowser: false,
      };
    }

  callback(null, user, context);
}

